import { useReducer } from "react";

import CardContext from "./cart-context";

const defaultCartContext = {
  items: [],
  totalAmount: 0,
};

const cartReducer = (state, action) => {
  if(action.type === "ADD_ITEM") {
    const updatedItems = state.items.concat(action.item);
    const updatedTotalAmount = state.totalAmount + action.item.price + action.item.amount;
    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    }
  }
  return  defaultCartContext;
};

const CartProvider = (props) => {
  const [state, dispatch] = useReducer(cartReducer, defaultCartContext);


    const addItemToCartHandler = (item) => {
      dispatch({
        type: "ADD_ITEM",
        item: item,
      });
    };

    const removeItemFromCartHandler = (id) => {
      dispatch({
        type: "REMOVE_ITEM",
        id: id,
      });
    };

    const cartContext = {
        items: state.items,
        totalAmount: state.totalAmount,
        addItem: (item) => addItemToCartHandler,
        removeItem: (id) => removeItemFromCartHandler,
    };

  return <CardContext.Provider value={cartContext}>{props.children}</CardContext.Provider>;
};

export default CartProvider;
